import Range from "./CardRange";
import { useEffect, useState } from "react";
import axios from "axios";


const Card = ({ data }) => {
  console.log(data)
  const [state, setState] = useState("disabled");

  useEffect(()=>{
    buttons(state)
  }, [state])

  const addFavorites = (e) => {
    setState("enabled")


  };
  const removeFavorites = (e) => {
    setState("disabled")
  }



  const buttons = (state) => {
    if(state === "enabled") {
      return  <i className="iLike iLike_press" onClick={removeFavorites}></i>
    }
    if(state === "disabled"){
      return <i className="iLike" onClick={addFavorites}></i>
    }

  }

  const imgUrl = "https://image.tmdb.org/t/p/w500";
  let date = "";
  let overview = `${data.overview.slice(0, 170)}...`;
  if (data.release_date) date = data.release_date.slice(0, 4);

  return (
    <>
      <div className="card movie_card ">
        <img
          src={`${imgUrl}${data.poster_path}`}
          className="card-img-top image"
          alt="Movie Poster"
        ></img>
        <div class="details">
          <div class="center">
            <h1>{data.title}</h1>
            <p>{overview}</p>
            <div>
            {buttons(state)}
            </div>


          </div>
        </div>
        <div className="card-body">
          <span className="movie_info float-left">
            <span className="movie_info">{date}</span>
            <h5 className="card-title">
              <strong>{data.title}</strong>
            </h5>
          </span>
          <Range range={data.vote_average} />
        </div>
      </div>
    </>
  );
};

export default Card;
