        axios 
         .get("/api")
         .then(res => setPopular(res.data.results))

         if(user.id){
             (user.favMoviesId).map(id => {
                axios
                .get(`api/favorites/${id}`)
                .then(res => console.log("res",res))
             })

         }




    },[])

   // console.log("favorites",favorites)
