import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import useInput from "../hooks/useInput";
import axios from "axios";



  const Community = () => {
    const friends = useInput();
    const navigate = useNavigate();
    const [searchedFriend, setSearchedFriend]= useState({});

    useEffect(()=>{
      Friend();
    },[searchedFriend] )

    const handleSubmit = (e) =>{
      e.preventDefault();
      const lowerFriend = (friends.value).toLowerCase()
      axios
        .get(`/api/community/${lowerFriend}`)
        .then(res => {
          setSearchedFriend(res.data[0])
          console.log(searchedFriend)
        })
    }

    const Friend = () => {
       return searchedFriend ? (
      <>
       <article class="postcard dark blue">
			<a class="postcard__img_link" href="#">
				<img class="postcard__img" src="https://picsum.photos/1000/1000" alt="Image Title" />
			</a>
			<div class="postcard__text">
				<h1 class="postcard__title blue">{searchedFriend.userName}</h1>
				<div class="postcard__subtitle small">
					<time datetime="2020-05-25 12:00:00">
						<i class="fas fa-calendar-alt mr-2"></i>Mon, May 25th 2020
					</time>
				</div>
				<div class="postcard__bar"></div>
				<div class="postcard__preview-txt">Lorem ipsum dolor sit amet consectetur adipisicing elit. Eligendi, fugiat asperiores inventore beatae accusamus odit minima enim, commodi quia, doloribus eius! Ducimus nemo accusantium maiores velit corrupti tempora reiciendis molestiae repellat vero. Eveniet ipsam adipisci illo iusto quibusdam, sunt neque nulla unde ipsum dolores nobis enim quidem excepturi, illum quos!</div>
				<ul class="postcard__tagbox">
					<li class="tag__item"><i class="fas fa-tag mr-2"></i>Podcast</li>
					<li class="tag__item"><i class="fas fa-clock mr-2"></i>55 mins.</li>
					<li class="tag__item play blue">
						<a href="#"><i class="fas fa-play mr-2"></i>Play Episode</a>
					</li>
				</ul>
			</div>
		</article>
          </>

      ) : (

        <div>
          <img src="https://cdn-icons.flaticon.com/png/512/3524/premium/3524032.png?token=exp=1646282789~hmac=b0631016908cabe409116103a56d2c76"  className="rounded-circle" />
          <p className="text-white" >Sorry! we couldn't find your friend in this dimension</p>        
      </div>

      )
    }


      return (
        <>
        <form class="form-inline" onSubmit={handleSubmit}>
            <input 
            {...friends}
            class="form-control mr-sm-2" 
            type="search" 
            placeholder="Find friends ej: TarantinoLover190" 
            aria-label="Search"/>
            <button class="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
         </form>
         <Friend/>
        </>
      )
  }

  export default Community;
