const Card = ({ data }) => {
  const imgUrl = "https://image.tmdb.org/t/p/w500"
    return (
      <div className="card">
        <div className="card-image">
          <figure className="image">
            <img
            src={data.poster_path ? `${imgUrl}${data.poster_path}` : "https://cdn.vox-cdn.com/thumbor/a5EcHSnHLRfQyzSFvhmPSnibCq0=/0x0:420x314/1400x1400/filters:focal(136x115:202x181):format(gif)/cdn.vox-cdn.com/uploads/chorus_image/image/55279403/tenor.0.gif"}
            alt="Placeholder image"
            />
          </figure>
        </div>
        <div className="card-content">
          <div className="media">
            <div className="media-left">
              <div className="media-content">
                <p className="title is-6">{data.original_title}</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  };

  export default Card;
