import { Link } from "react-router-dom";

const Sidebar = () => {
  const genersId=[{id:28, name:"Action", icon: "fa-solid fa-gun"},{id:18, name:"Drama",icon: "fa-solid fa-masks-theater"},{id:35, name:"Comedy", icon:"fa-regular fa-face-grin-squint-tears" },{id:80, name:"Crime", icon: " fa-solid fa-user-secret"},{id:878, name:"Science Fiction", icon: "fa-solid fa-shuttle-space"},{id:10762, name: "Kids",icon:"fa-solid fa-baby "},{id:10749, name: "Romance", icon:"fa-solid fa-heart"},{id:99, name: "Documentary", icon:"fa-solid fa-book "},{id:99, name: "Horror",icon:"fa-solid fa-spider"},{id:16, name: "Animation", icon:"fa-solid fa-user-ninja"},{id: 10402,name:"Music",icon:"fa-solid fa-guitar"},{id: 14, name:"Fantasy", icon:"fa-solid fa-dragon"}]
  return (
    <div className="">
      <nav class="main-menu">
              {genersId.map((geners,i)=> (
                <li class="darkerli" key={i}>
                   <a href={`/geners/${geners.id}`}>
                      <i class={`fa faIcon fa-lg ${geners.icon}`}></i>
                      <span class="nav-text">{geners.name}</span>
                   </a>
                </li>
              ))}
      </nav>
    </div>
  );
};

export default Sidebar;
