import { Link, useNavigate } from "react-router-dom";
import useInput from "../hooks/useInput";
import axios from "axios";
import { useContext } from "react";
import { UserContext } from "..";

const Login = () => {
  const email = useInput();
  const password = useInput();
  const { setUser } = useContext(UserContext);
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("/api/login", {
        email: email.value,
        password: password.value,
      })
      .then((res) => {
        setUser(res.data);
        navigate("/upcomingMovies");
      })
      .catch((err) =>
        alert("The email or password does not match, please try again")
      );
  };

  //intento de autenticacion con google
  /*  const handleClick = () => {
      axios
        .get("/api/login/google")
        .then(res => console.log(res))
   } */

  return (
    <section className="top">
      <div className="container h-50">
        <div className="row d-flex justify-content-center align-items-center h-50">
          <div className="col-12 col-sm-9 col-sm-7 col-xl-6">
            <div className="card cardColor" style={{ borderRadius: "15px" }}>
              <div className="card-body p-4">
                <h2 className="text-uppercase text-center ">Login</h2>

                <form onSubmit={handleSubmit}>

                  <div className="form-outline mb-2">

                    <input
                      {...email}
                      type="email"
                      id="form3Example3cg"
                      className="form-control form-control-sm"
                    />
                    <label className="form-label">Your Email</label>

                  </div>

                  <div className="form-outline mb-2">

                    <input
                      {...password}
                      type="password"
                      id="form3Example4cg"
                      className="form-control form-control-sm"
                    />
                    <label className="form-label">Password</label>

                  </div>

                  <div className="d-flex justify-content-center">
                    <button type="submit" className="btn btn-success btn-block btn-lg gradient-custom-4 text-body">Login</button>
                  </div>

                  {/* <a href=""  onClick={handleClick} class="button">Sign in with Google</a> */}

                  <p className="text-center text-muted mt-5 mb-0">
                    You do not have an account?
                    <Link to={"/register"}><u> Register here</u></Link>
                  </p>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Login;
