import axios from "axios";
import React, { useState } from "react";
import useInput from "../hooks/useInput";
import Grid from "./Grid";

const Navbar = () => {
  const navbarSearch = useInput();
  const [result, setResult] = useState([]);

  const handleSubmit=(e)=> {
    e.preventDefault();
    const searchFromUser = navbarSearch.value;
    //example: Jack Reacher -> jack+reacher
    const searchFormatted = searchFromUser.replace(/\s+/g, '+').toLowerCase();
    axios
        .get(`http://localhost:3001/api/search/${searchFormatted}`)
        .then(res => res.data)
        .then(searched => {
            setResult(searched.results)
            console.log(searched.results)
        })

  }

  return (
    <>
    <nav class="navbar navbar-expand-lg navbar-light bg-light">
  <a class="navbar-brand" href="#">LOOP</a>
  <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
    <span class="navbar-toggler-icon"></span>
  </button>

  <div class="collapse navbar-collapse" id="navbarSupportedContent">
    <ul class="navbar-nav mr-auto">
      <li class="nav-item">
        <a class="nav-link" href="#">Movies</a>
      </li>
      <li class="nav-item dropdown">
        <a class="nav-link dropdown-toggle" href="#" id="navbarDropdown" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
          Dropdown
        </a>
        <div class="dropdown-menu" aria-labelledby="navbarDropdown">
          <a class="dropdown-item" href="#">Action</a>
          <a class="dropdown-item" href="#">Another action</a>
          <div class="dropdown-divider"></div>
          <a class="dropdown-item" href="#">Something else here</a>
        </div>
      </li>
      <li class="nav-item">
        <a class="nav-link disabled" href="#">Disabled</a>
      </li>
    </ul>
      {/*INPUT*/}
      <div className="container-2">
     <span className="icon">
       <i className="fa fa-search"></i>
     </span>
     <form onSubmit={handleSubmit}>
     <input 
     {...navbarSearch}
     type="search" 
     id="search" 
     placeholder="Search..." />
     </form>
   </div>
  </div>
</nav>
<Grid result={result}/>

   </>
  );
};

export default Navbar;
