import axios from "axios";
import React, { useState } from "react";
import useInput from "../hooks/useInput";
import Grid from "./Grid";

const Navbar = () => {
  const navbarSearch = useInput();
  const [result, setResult] = useState([]);

  const handleSubmit=(e)=> {
    e.preventDefault();
    const searchFromUser = navbarSearch.value;
    //example: Jack Reacher -> jack+reacher
    const searchFormatted = searchFromUser.replace(/\s+/g, '+').toLowerCase();
    axios
        .get(`http://localhost:3001/api/search/${searchFormatted}`)
        .then(res => res.data)
        .then(searched => {
            setResult(searched.results)
            console.log(searched.results)
        })

  }

  return (
      <>
 <nav class="navbar navbar-light bg-light justify-content-between">
  <a class="navbar-brand">Navbar</a>
  <form class="form-inline">
    <button class="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
    <button class="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
  </form>
</nav>
<Grid result={result}/>


</>
  );
};

export default Navbar;

