const Card = ({ data }) => {


  const imgUrl = "https://image.tmdb.org/t/p/w500";
  let date = ""
  let overview = `${data.overview.slice(0,170)}...`
  if (data.release_date) date = data.release_date.slice(0,4)

    return (
      <>
        <div className="card movie_card ">
          <img
            src={`${imgUrl}${data.poster_path}`}
            className="card-img-top image"
            alt="Movie Poster"
          ></img>
          <div class="details">
          <div class="center">
            <h1>{data.title}<span>{data.vote_average}</span></h1>
            <p>{overview}</p>
            <ul>
              <li><a href="#"><i class="fa fa-star text-warning" aria-hidden="true">Add Fav</i></a></li>

            </ul>
          </div>
        </div>
          <div className="card-body">
          <span className="movie_info float-left">
              <i className="fas fa-star float-left"></i> {data.vote_average}
            </span>
            <h5 className="card-title">{data.title}</h5>
            <span className="movie_info">{date}</span>

          </div>
        </div>
      </>
    );

  return <></>

};

export default Card;
