import React, { useState, useContext } from "react";
import axios from "axios";
import useInput from "../hooks/useInput";
import Buttons from "./Buttons";
import { UserContext } from "..";
import Search from "../common/Search";
import { Link, useNavigate } from "react-router-dom";

const Navbar = () => {
    const {user} = useContext(UserContext);

const favButton =() => {
    if(user.id) {
       return(
        <>
         <Link to={"/favorites"}>
           <img  width={"50"} src="https://cdn-icons.flaticon.com/png/512/4208/premium/4208408.png?token=exp=1646001513~hmac=c70504d9ea61c0d006014897812aca0b"/>
        </Link>

        </>
       ) 
    }
}
  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <Link to={"/upcoming"}>
        <a className="navbar-brand" href="#"> LOOP</a>
        </Link>
        <button
          className="navbar-toggler"
          type="button"
          data-toggle="collapse"
          data-target="#navbarNavAltMarkup"
          aria-controls="navbarNavAltMarkup"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNavAltMarkup">
          <div className="navbar-nav">
            <Link to={"popularMovies"}>
              <a className="nav-item nav-link" href="#">Movies</a>
            </Link>
            <Link to={"series"}>
              <a className="nav-item nav-link" href="#">Series</a>
            </Link>
            <Link to={"user/community"}>
              <a className="nav-item nav-link " href="#">Community </a>
            </Link>

            {favButton()}
          </div>
        </div>
        <Search/>
     <Buttons/>
      </nav>
    </>
  );
};

export default Navbar;
