//se fija el valor y pinta los corazones segun el rango correspondiendte 
const CardRange = ({range}) => {
    let num = 0;
    const heart = <i class="fa-regular fa-star"></i>
    const coloredHeart = <i class="fa-solid fa-star rating-color"></i>
    let rating = [heart,heart,heart,heart,heart]


 if(range>0 && range<2)num=1
 if(range>=2 && range<4)num=2
 if(range>=4 && range<6)num=3
 if(range>=6 && range<8)num=4
 if(range>=8 && range<=10)num=5

 //cambia la className de los corazones
    for(let i=0; i<num; i++){
        rating[i]=coloredHeart
    }

    return(
        <>
             {rating.map(star => (star))}
             <i id="range">{range}</i>
        </>

    )
}

export default CardRange;
