import axios from "axios";
import React, { useState } from "react";
import useInput from "../hooks/useInput";
import Grid from "./Grid";
import { Link } from "react-router-dom";

const Navbar = () => {
    /////////////////////////////////// LOGICA A LLEVAR A SEARCH \\\\\\\\\\\\\\\\\\\\\\\\
  const navbarSearch = useInput();
  const [result, setResult] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const searchFromUser = navbarSearch.value;
    //example: Jack Reacher -> jack+reacher
    const searchFormatted = searchFromUser.replace(/\s+/g, "+").toLowerCase();
    axios
      .get(`http://localhost:3001/api/search/${searchFormatted}`)
      .then((res) => res.data)
      .then((searched) => {
        setResult(searched.results);
        console.log(searched.results);
      });

    e.target.value = "";
  };
/////////////////////// LOGICA A LLEVAR A SEARCH \\\\\\\\\\\\\\\\\\\\\\\
  return (
    <>
      <nav class="navbar navbar-expand-lg navbar-dark bg-dark">
        <a class="navbar-brand" href="#">
          LOOP
        </a>
        <button
          class="navbar-toggler"
          type="button"
          data-toggle="collapse"
          data-target="#navbarNavAltMarkup"
          aria-controls="navbarNavAltMarkup"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span class="navbar-toggler-icon"></span>
        </button>
        <div class="collapse navbar-collapse" id="navbarNavAltMarkup">
          <div class="navbar-nav">
            <a class="nav-item nav-link" href="#">
              Movies
            </a>
            <a class="nav-item nav-link" href="#">
              Series
            </a>
            <a class="nav-item nav-link disabled" href="#">
              Disabled
            </a>
          </div>
        </div>

        <div className="container-2 space">
          <span className="icon">
            <i className="fa fa-search"></i>
          </span>
          <form onSubmit={handleSubmit}>
            <input
              {...navbarSearch}
              type="search"
              id="search"
              placeholder="Search..."
            />
          </form>
        </div>
        <button class="btn btn-light">
          LogIn
        </button>
       <Link to={"/register"}>
        <button class="btn btn-light" >
          Register
        </button>
        </Link>

      </nav>
      <Grid result={result}/>
    </>
  );
};

export default Navbar;
