import { useState } from "react";
import useInput from "../hooks/useInput";
import axios from "axios";



  const Community = () => {
    const friends = useInput();
    const [searchedFriend, setSearchedFriend]= useState("");


    const handleSubmit = (e) =>{
      e.preventDefault();
      const lowerFriend = (friends.value).toLowerCase()
      axios
        .get(`/api/community/${lowerFriend}`)
        .then(res => {
          setSearchedFriend(res.data[0])
        })
    }

//por defecto aparecera que no pudimos encontrar a tu amigo (a mejorar! + poder ver los fav del usuario que busco )
    const Friend = () => {
       return searchedFriend ? (
      <>
    <div class=" box cardCom rounded shadow-sm py-3 px-4">
      <img src="https://cdn-icons.flaticon.com/png/512/3280/premium/3280979.png?token=exp=1646285331~hmac=82bf783eb79d4686b33183e7b9676aa2" alt="" width="100" class="img-fluid rounded-circle mb-3 img-thumbnail shadow-sm"/>
                <h4 class="mb-0">{searchedFriend.userName}</h4>
                <p>{searchedFriend.email}</p>
                <span class="small text-muted">Lorem ipsum dolor sit amet consectetur adipisicing elit. Ducimus, veniam.</span>
            </div>
          </>

      ) : (

        <div className="box">
          <img src="https://cdn-icons.flaticon.com/png/512/3524/premium/3524032.png?token=exp=1646282789~hmac=b0631016908cabe409116103a56d2c76"  className="rounded-circle" />
          <p className="textGeneral" >Sorry! we couldn't find your friend in this dimension</p>        
      </div>

      )
    }


      return (
        <div className="containerGrid">
        <form class="form-inline formUsers topCom" onSubmit={handleSubmit}>
            <input 
            {...friends}
            class="form-control mr-sm-2" 
            type="search" 
            placeholder="Find friends ej: TarantinoLover190" 
            aria-label="Search"/>
         </form>
        <Friend/>

        </div>
      )
  }

  export default Community;
