import React, { useEffect, useState } from "react";
import Navbar from "./components/Navbar";
import Sidebar from "./components/Sidebar";
import Register from "./components/Register";
import Login from "./components/Login";
import Content from "./components/Content";


import { Routes,Route, useMatch, useParams } from "react-router-dom";
import Community from "./components/comunnity";
import Grid from "./components/Grid";
import axios from "axios";

const App = () => {
    const {id} = useParams();
    const match = useMatch("/moviesGener/:id")
    const [moviesGener,setMoviesGener]= useState([])
    useEffect(()=>{
        axios
        .get(`api/movies/geners/${id}`)
        .then(res => {
           console.log(res.data)
           setMoviesGener(res.data.results)
        })
    },[match])

    return(
        <>
        <Navbar/>
        <Sidebar/>
        <div className="containerGrid">
                <Routes>
                    <Route path="/" element={<Content/>}/> 
                    <Route path="/:type" element={<Content/>}/> 
                    <Route path="/moviesGener/:id" element={<Grid moviesGener={moviesGener} />}/>

                </Routes>    
        </div>
                <Routes>
                    <Route path="/user/register" element={<Register/>}/>
                    <Route path="/user/login" element={<Login/>}/>
                    <Route path="/user/community" element={<Community/>}/>
                </Routes> 


        </>
    )
}

export default App;
