import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import useInput from "../hooks/useInput";
import axios from "axios";



  const Community = () => {
    const friends = useInput();
    const navigate = useNavigate();
    const [searchedFriend, setSearchedFriend]= useState({});

    useEffect(()=>{
      Friend();
    },[searchedFriend] )

    const handleSubmit = (e) =>{
      e.preventDefault();
      const lowerFriend = (friends.value).toLowerCase()
      axios
        .get(`/api/community/${lowerFriend}`)
        .then(res => {
          setSearchedFriend(res.data[0])
          console.log(searchedFriend)
        })
    }

    const Friend = () => {
       return searchedFriend ? (
      <>
    <div class="bg-white rounded shadow-sm py-5 px-4">
      <img src="https://bootstrapious.com/i/snippets/sn-team/teacher-4.jpg" alt="" width="100" class="img-fluid rounded-circle mb-3 img-thumbnail shadow-sm"/>
                <h5 class="mb-0">{searchedFriend.userName}</h5><span class="small text-uppercase text-muted">CEO - Founder</span>
                <ul class="social mb-0 list-inline mt-3">
                    <li class="list-inline-item"><a href="#" class="social-link"><i class="fa fa-facebook-f"></i></a></li>
                    <li class="list-inline-item"><a href="#" class="social-link"><i class="fa fa-twitter"></i></a></li>
                    <li class="list-inline-item"><a href="#" class="social-link"><i class="fa fa-instagram"></i></a></li>
                    <li class="list-inline-item"><a href="#" class="social-link"><i class="fa fa-linkedin"></i></a></li>
                </ul>
            </div>
          </>

      ) : (

        <div>
          <img src="https://cdn-icons.flaticon.com/png/512/3524/premium/3524032.png?token=exp=1646282789~hmac=b0631016908cabe409116103a56d2c76"  className="rounded-circle" />
          <p className="text-white" >Sorry! we couldn't find your friend in this dimension</p>        
      </div>

      )
    }


      return (
        <>
        <form class="form-inline" onSubmit={handleSubmit}>
            <input 
            {...friends}
            class="form-control mr-sm-2" 
            type="search" 
            placeholder="Find friends ej: TarantinoLover190" 
            aria-label="Search"/>
            <button class="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
         </form>
         <Friend/>
        </>
      )
  }

  export default Community;
