import axios from "axios";
import React, { useState } from "react";
import useInput from "../hooks/useInput";
import Grid from "./Grid";

const Navbar = () => {
  const navbarSearch = useInput();
  const [result, setResult] = useState([]);

  const handleSubmit=(e)=> {
    e.preventDefault();
    const searchFromUser = navbarSearch.value;
    //example: Jack Reacher -> jack+reacher
    const searchFormatted = searchFromUser.replace(/\s+/g, '+').toLowerCase();
    axios
        .get(`http://localhost:3001/api/search/${searchFormatted}`)
        .then(res => res.data)
        .then(searched => {
            setResult(searched.results)
            console.log(searched.results)
        })

  }

  return (
      <>
 <nav class="navbar navbar-expand-lg navbar-light bg-light">
  <a class="navbar-brand" href="#">LOOP</a>
  <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
    <span class="navbar-toggler-icon"></span>
  </button>
  <div class="collapse navbar-collapse" id="navbarNavAltMarkup">
    <div class="navbar-nav">
      <a class="nav-item nav-link" href="#">Movies</a>
      <a class="nav-item nav-link" href="#">Series</a>
      <a class="nav-item nav-link disabled" href="#">Disabled</a>
    </div>
  </div>
  <button class="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
    <button class="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
</nav>
<Grid result={result}/>


</>
  );
};

export default Navbar;

