import axios from "axios";
import React, { useState } from "react";
import useInput from "../hooks/useInput";
import Grid from "./Grid";
import Buttons from "./Buttons";
import { useContext } from "react";
import { UserContext } from "..";

const Navbar = () => {
    const {user} = useContext(UserContext);
    /////////////////////////////////// LOGICA A LLEVAR A SEARCH \\\\\\\\\\\\\\\\\\\\\\\\
  const navbarSearch = useInput();
  const [result, setResult] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const searchFromUser = navbarSearch.value;
    //example: Jack Reacher -> jack+reacher
    const searchFormatted = searchFromUser.replace(/\s+/g, "+").toLowerCase();
    axios
      .get(`http://localhost:3001/api/search/${searchFormatted}`)
      .then((res) => res.data)
      .then((searched) => {
        setResult(searched.results);
      });

    e.target.value = "";
  };
/////////////////////// LOGICA A LLEVAR A SEARCH \\\\\\\\\\\\\\\\\\\\\\\

const favButton =() => {
    if(user.id) {
       return(
        <>
         <img  width={"50"} src="https://cdn-icons.flaticon.com/png/512/4208/premium/4208408.png?token=exp=1646001513~hmac=c70504d9ea61c0d006014897812aca0b"/>
        </>
       ) 
    }
}
  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <a className="navbar-brand" href="#">
          LOOP
        </a>
        <button
          className="navbar-toggler"
          type="button"
          data-toggle="collapse"
          data-target="#navbarNavAltMarkup"
          aria-controls="navbarNavAltMarkup"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNavAltMarkup">
          <div className="navbar-nav">
            <a className="nav-item nav-link" href="#">
              Movies
            </a>
            <a className="nav-item nav-link" href="#">
              Series
            </a>
            <a className="nav-item nav-link disabled" href="#">
              Disabled
            </a>
            {favButton()}
          </div>
        </div>


        <div className="container-2 space">
        <img width={"30"} className="icon" src="https://cdn-icons-png.flaticon.com/512/872/872355.png"/>
          <form onSubmit={handleSubmit}>
            <input
              {...navbarSearch}
              type="search"
              id="search"
              placeholder="Search..."
            />
          </form>
        </div>
     <Buttons/>

      </nav>
      <Grid result={result}/>
    </>
  );
};

export default Navbar;
