import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import useInput from "../hooks/useInput";
import axios from "axios";



  const Community = () => {
    const friends = useInput();
    const navigate = useNavigate();
    const [searchedFriend, setSearchedFriend]= useState([]);

    useEffect(()=>{
      Friend();
    },[searchedFriend] )

    const handleSubmit = (e) =>{
      e.preventDefault();
      const lowerFriend = (friends.value).toLowerCase()
      axios
        .get(`/api/community/${lowerFriend}`)
        .then(res => setSearchedFriend(res.data))
    }

    const Friend = () => {
       return searchedFriend.userName ? (
        <div className="card movie_card ">
        <img
          src="https://cdn-icons-png.flaticon.com/512/3300/3300405.png"
          className="card-img-top image"
        ></img>
        <div className="card-body">
          <span className="movie_info float-left">
            <h5 className="card-title">
              <strong>{searchedFriend.userName}</strong>
            </h5>
            <p>{searchedFriend.email}</p>
          </span>
        </div>
      </div>
      ) : (

        <div>
          <img src="https://cdn-icons.flaticon.com/png/512/3524/premium/3524032.png?token=exp=1646282789~hmac=b0631016908cabe409116103a56d2c76"  className="rounded-circle" />
          <p className="text-white" >Sorry! we couldn't find your friend in this dimension</p>        
      </div>

      )
    }


      return (
        <>
        <form class="form-inline" onSubmit={handleSubmit}>
            <input 
            {...friends}
            class="form-control mr-sm-2" 
            type="search" 
            placeholder="Find friends ej: TarantinoLover190" 
            aria-label="Search"/>
            <button class="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
         </form>
         <Friend/>
        </>
      )
  }

  export default Community;
