import axios from "axios";
import React, { useEffect, useState, useContext } from "react";
import { useParams } from "react-router-dom";
import { UserContext } from "..";
import Grid from "./Grid";


const Content = () =>{
    const { user, searched} = useContext(UserContext)
    const [popularMovies, setPopularMovies] = useState([]);
    const [favorites, setFavorites] = useState([]);
    const [series, setSeries] = useState([]);
    const [upcomingMovies, setUpcomingMovies] = useState([]);
    const {id}=useParams();



    useEffect(()=>{

        axios
         .get("api/movies/upcoming")
         .then(res => setUpcomingMovies(res.data.results))

        axios 
         .get("/api/movies/popular")
         .then(res => setPopularMovies(res.data.results))

       /*  axios
         .get(`api/movies/geners/${id}`)
         .then(res => )
 */
         axios
         .get("api/series")
         .then(res => setSeries(res.data.results))



    },[])

 ///seting favs movies only if the user is login
    //ypu can't see series in favs in this proyect (to be continue... )
    useEffect(()=>{
        if(user.id){
            let favMovies = [];

            (user.favMoviesId).map((id,i) => {
                axios
                .get(`api/movies/single/${id}`)
                .then(res => {
                    favMovies.push(res.data);
                 })
            })
            setFavorites(favMovies)

        }
    },[(user.favMoviesId)])

    return (


           <Grid 
           favorites={favorites} 
           popularMovies={popularMovies} 
           series={series} 
           upcomingMovies={upcomingMovies} 
           search={searched}/>
    )
}

export default Content;
