const Grid = ({result}) => {
    let onlyWithImg = result.filter(movie => movie.poster_path)
    console.log(onlyWithImg)
  return (
    <div className="container">
        <div className="row">
      {onlyWithImg.map((data, i) => (
        <div className="col-3" key={i}>
          <Card data={data} />
        </div>
      ))}
      </div>
    </div>
  );
};

export default Grid;
