import axios from "axios";
import React, { useState } from "react";
import useInput from "../hooks/useInput";
import Grid from "./Grid";

const Navbar = () => {
  const navbarSearch = useInput();
  const [result, setResult] = useState([]);

  const handleSubmit=(e)=> {
    e.preventDefault();
    const searchFromUser = navbarSearch.value;
    //example: Jack Reacher -> jack+reacher
    const searchFormatted = searchFromUser.replace(/\s+/g, '+').toLowerCase();
    axios
        .get(`http://localhost:3001/api/search/${searchFormatted}`)
        .then(res => res.data)
        .then(searched => {
            setResult(searched.results)
            console.log(searched.results)
        })

  }

  return (
    <>
     {/*INPUT*/}
     <div className="container-2">
     <span className="icon">
       <i className="fa fa-search"></i>
     </span>
     <form onSubmit={handleSubmit}>
     <input 
     {...navbarSearch}
     type="search" 
     id="search" 
     placeholder="Search..." />
     </form>
   </div>
    <Grid result={result}/>
   </>
  );
};

export default Navbar;
